// Filtrer les résultats en fonction de la recherche
const searchResults = ref({
  playlists: [],
  artists: [],
  albums: []
});

const onSearch = () => {
  console.log("🔍 Fonction onSearch appelée !", searchQuery.value);
  const query = searchQuery.value.trim().toLowerCase();

  // Recherche dans les playlists
  searchResults.value.playlists = playlists.value.filter(p => {
    const result = p.name.toLowerCase().includes(query);
    if (result) {
      console.log("Playlist trouvée:", p.name);  // Log si une playlist correspond
    }
    return result;
  });

  // Recherche dans les artistes
  searchResults.value.artists = artists.value.filter(a => {
    const result = a.name.toLowerCase().includes(query);
    if (result) {
      console.log("Artiste trouvé:", a.name);  // Log si un artiste correspond
    }
    return result;
  });

  // Recherche dans les albums
  searchResults.value.albums = albums.value.filter(a => {
    const result = a.name.toLowerCase().includes(query);
